function prefetchTYPAssets() {
    var imageAssets = new Array();
    var jsCssAssets = new Array();
      imageAssets.push("https://images-eu.ssl-images-amazon.com/images/G/31/x-locale/common/buy-buttons/review-1-click-order._CB138348768_.gif");
      imageAssets.push("https://images-eu.ssl-images-amazon.com/images/G/31/x-locale/common/buttons/continue-shopping._CB138360611_.gif");
      imageAssets.push("https://images-eu.ssl-images-amazon.com/images/G/31/x-locale/common/buy-buttons/thank-you-elbow._CB138348794_.gif");
      imageAssets.push("https://images-eu.ssl-images-amazon.com/images/G/31/x-locale/communities/social/snwicons_v2._CB402380004_.png");
      imageAssets.push("https://images-eu.ssl-images-amazon.com/images/G/31/checkout/assets/carrot._CB138350193_.gif");
      imageAssets.push("https://images-eu.ssl-images-amazon.com/images/G/31/checkout/thank-you-page/assets/yellow-rounded-corner-sprite._CB166613107_.gif");

    // pre-fetching image assets
    for (var i=0; i<imageAssets.length; i++) {
       new Image().src = imageAssets[i];
    }
    // pre-fetching css and js assets based on different browser types
    var isIE = /*@cc_on!@*/0;
    var isFireFox = /Firefox/.test(navigator.userAgent);
    if (isIE) {
      for (var i=0; i<jsCssAssets.length; i++) {
        new Image().src = jsCssAssets[i];
      }
    }
    else if (isFireFox) {
      for (var i=0; i<jsCssAssets.length; i++) {
        var o =  document.createElement("object");
        o.data = jsCssAssets[i];
        o.width = o.height = 0;
        document.body.appendChild(o);
      }
    }
}

    var onload = function () {
        setTimeout(prefetchTYPAssets, 2000);
    };
    if (window.addEventListener) {
        window.addEventListener("load", onload);
    } else if (window.attachEvent) { /* for <= IE 8 */
        window.attachEvent("onload", onload);
    }

