    !function() {
      ue.count('sx.cf.bounds-support', document.documentElement.getBoundingClientRect ? 1 : 0);

      var $, docElem = document.documentElement, body = document.body, imageLoadRecs = [], curResCount = -1;


      function getPositionInDocument(elem) {
        if (!elem.getBoundingClientRect) {
	  return undefined;
	}
        var box = elem.getBoundingClientRect(),
            scrollTop = window.scrollY || window.pageYOffset || docElem.scrollTop || body.scrollTop,
            scrollLeft = window.scrollX || window.pageXOffset || docElem.scrollLeft || body.scrollLeft,
            clientTop = docElem.clientTop || body.clientTop || 0,
            clientLeft = docElem.clientLeft || body.clientLeft || 0,
            bottom = box.bottom +  scrollTop - clientTop,
            right = box.right + scrollLeft - clientLeft;
        return { bottom: Math.round(bottom), right: Math.round(right) };
      }


      function inInitialViewport (image) {
        var imageBounds = getPositionInDocument(image);
        return (imageBounds == undefined)
	  || ((imageBounds.bottom <= docElem.clientHeight) && (imageBounds.right <= docElem.clientWidth));
      }

      function viewCompleteImageLoaded(image, time, resCount, isAjax) {
        var i, imageLoad, cfTime = -1, cfImageCnt = 0;


        if (image.className.indexOf('cfMarker') > -1) {
          imageLoadRecs.push({"image": image, "time": time});
        }


        if (curResCount !== -1 && curResCount !== resCount) {
          ue.count('sx.cf.res-count-misalignment', resCount);
        }
        curResCount = resCount;


        if (imageLoadRecs.length !== resCount) {return;}


        for (i = 0; i < resCount; i++) {
          imageLoad = imageLoadRecs[i];
          if (inInitialViewport(imageLoad.image)) {
            cfImageCnt++;
            if (imageLoad.time > cfTime) {cfTime = imageLoad.time;}
          }
        }


        if (cfTime <= 0) {cfTime = +new Date;}


        uet('cf', isAjax ? $ && $.searchUE && $.searchUE.getScope() : undefined, undefined, cfTime);


        isAjax && $ && $.searchUE && $.searchUE.manageLoad();


        ue.count('cfImageCount', cfImageCnt);


        imageLoadRecs = [];
        curResCount = -1;
      }


      function afterJQueryAvailable(jQuery) {
        $ = jQuery || window.jQuery;
        $ && $(document).bind('before-searchajax', function() {
          if (imageLoadRecs.length) {

            ue.count('sx.cf.images-loads-missed', curResCount - imageLoadRecs.length);


            imageLoadRecs = [];
            curResCount = -1;
          }
        });
      }
      window.P && window.P.when('jQuery').execute(afterJQueryAvailable);


